/**
 * Returns the foones itself.
 *
 * @param {string} name Your foo name
 * @param {boolean} [great] If you are feeling great today
 * @returns {string} The foones itself
 */
export function foo(name, great = true) {
  return `You are ${name} van foo and you are feeling ${
    great ? "great" : "bad"
  } today.
  `;
}
