/**
 * Returns the nomness itself.
 *
 * @param {string} name Your nom name
 * @param {boolean} [great] If you are feeling great today
 * @returns {string} The nomness itself
 */
export function nom(name, great = true) {
  return `You are ${name} van nom and you are feeling ${
    great ? "great" : "bad"
  } today.
  `;
}
